// page init
jQuery(function(){
	initTabs();
	initSlideShow();
	initTouchNav();
	initLayout();
});

// fade gallery init
function initSlideShow() {
	jQuery('div.slideshow').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		event: 'click',
		autoRotation: true,
		autoHeight: true,
		switchTime: 8000,
		animSpeed: 500
	});
}

// init layout
function initLayout() {
	ResponsiveHelper.addRange({
		'..767': {
			on: function() {
				initSlideMenu();
				initAccordion();
			},
			off: function() {
				var slideBlock = jQuery('.open-close .slide').css({
					display:''
				}),
					contentBlock = jQuery('#wrapper .w1'),
					holder = jQuery('#header .open-close'),
					opener = holder.find('.opener'),
					activeClass = 'active';
				opener.off();
				holder.removeClass(activeClass);
				holder.css('right', '');
				contentBlock.css({
					marginLeft: 0,
					paddingRight: 0
				});
				var block = jQuery('.drop-left .block, #nav li'),
					slideLists = block.find('.slide-list, .dropdown');
					block.removeClass('active');
					slideLists.removeAttr('style');
			}
		}
	});
}

// accordion menu init
function initAccordion() {
	jQuery('.drop-left').slideAccordion({
		opener: '.title a',
		slider: '.slide-list',
		animSpeed: 300
	});
	jQuery('#nav ul').slideAccordion({
		opener: '.open',
		slider: '.dropdown',
		animSpeed: 300
	});
}

// init slide menu
function initSlideMenu() {
	var slideBlock = jQuery('.open-close .slide'),
		contentBlock = jQuery('#wrapper .w1'),
		holder = jQuery('#header .open-close'),
		opener = holder.find('.opener, .close'),
		activeClass = 'active',
		animSpeed = 400;

	function slideMenu() {
		opener.on('click', function(e) {
			e.preventDefault();
			changeState();
		});

		var slideBlockOffset = slideBlock.outerWidth();

		if (holder.hasClass(activeClass)) {
			holder.css({
				right: 0
			});
		} else{
			holder.css({
				display: 'block',
				right: -slideBlockOffset
			});
		}
		function changeState() {
			if (holder.hasClass(activeClass)) {
				holder.removeClass(activeClass);
				holder.stop().animate({
					right: -slideBlockOffset
				}, animSpeed);
				contentBlock.stop().animate({
					paddingRight: 0,
					marginLeft: 0
				}, animSpeed);
			} else {
				holder.addClass(activeClass);
				holder.stop().animate({
					right: 0
				}, animSpeed);
				contentBlock.stop().animate({
					paddingRight: slideBlockOffset,
					marginLeft: -slideBlockOffset
				}, animSpeed);
			}
		}
	}
	slideMenu();
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').contentTabs({
		tabLinks: 'a'
	});
}

// open-close init
function initOpenClose() {
	jQuery('#wrapper').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '.opener,.close',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

